INSERT INTO `_answers` VALUES (1,'Хобіт, якого викинуло з буденного життя',1,1,1),(2,'Дівчинка, яку послали на криваве бойовище',1,2,1),(3,'Секретарка, яку закинуло в магічний світ',1,3,1),(4,'Фермер, який вирощує магічних свиней',1,4,1),(5,'Так, це звична річ',2,4,0.5),(6,'Так, він же особливий',2,1,1),(7,'Ні, закохуються не через магію',2,3,1),(8,'Так, через неї на нього полюють',2,2,1),(9,'Відмовився і боровся би з ним',3,1,1),(10,'Погодився, зрадив і зайняв його місце',3,2,1),(11,'Героїня б закохалась, бо всередині він добрий',3,3,1),(12,'Так, він завжди бореться зі злом',4,1,1),(13,'Так, і те, що він герой, не означає, що він має вижити в цих битвах',4,2,1),(14,'Ні, він садить моркву на городі',4,4,1),(15,'Начхав би нього, бо нема йому ще чим зайнятись',3,4,1),(16,'Ні, він надто зайнятий сердечними почуттями',4,3,1),(17,'Піде рятувати світ',5,1,1),(18,'Тяжко сказати, якщо врахувати, що він цей кінець і запустив',5,2,1),(19,'Приділить останні хвилини коханій людині',5,3,1),(20,'Обговорить це з друзями за чашкою кави',5,4,1),(21,'Так, звісно',6,1,1),(22,'Так, але не факт, що він доживе до самого кінця',6,2,0.5),(23,'Ні, нема йому що робити, тільки з хати виходити',6,4,1),(24,'Так, авжеж',7,1,1),(25,'Ні, він має його нищити',7,2,1),(26,'Лише заради коханої людини',7,4,1),(27,'Так, він же герой',8,1,1),(28,'Емм... він має подолати сам себе?',8,2,1),(29,'Ні, ліпше закохати його в себе',8,3,1),(30,'Угу, саме так',13,1,1),(31,'Угу, пророцтва про знищення світу',13,2,1),(32,'Ні, ще чого',13,4,0.5),(33,'Так, звісно',9,1,1),(34,'Так, проблема лише в тому, що решта світу різниться з ним в понятті правильності',9,2,1),(35,'Тьфу ти, нема йому, що робити, тільки думати, що правильно',9,4,1),(36,'Угу, постійно',10,2,1),(37,'Так, і тільки прекрасний любовний інтерес вирве його звідси',10,3,1),(38,'Ні, герой прориває цю кляту завісу мряки та зла!',10,1,1),(39,'Ну, він же не винен, що живе на болоті',10,4,1),(40,'Так, легко',11,2,1),(41,'Ні, в жодному разі!',11,1,1),(42,'Може, але кохання змінить його',11,3,1),(43,'Так, щонайбільшу',12,2,1),(44,'Так, і тільки кохання зцілить його',12,3,1),(45,'А дзуськи, передові науковці винайшли зілля проти психологічних травм',12,4,1),(46,'Так, обов\'язково',14,3,1),(47,'Так, принаймні вона так вдає, щоб підібратися ближче і всадити ножа в спину',14,2,1),(48,'Та ні, в неї нема часу, бо треба рятувати світ',14,1,1),(49,'Так, безперечно',15,3,1),(50,'Так, це обов\'язковий ефект шлюбного поєднання душ в цьому регіоні',15,4,1),(51,'Угу, в її шизофренічних мріях',15,2,1),(52,'Так, легко',16,3,1),(53,'Ні, швидше він змінить її та перетворить в чудовисько',16,2,1),(55,'Може, але не хоче, їй ліньки',16,1,0.5),(56,'Так, це майже напевне',17,3,1),(57,'Ні в якому разі, героїня має здолати його та врятувати від нього світ',17,1,1),(58,'Так, укладати шлюби можна тільки за таких умов (витяг із законодавства)',17,4,1),(59,'Так, магічний інтернет ловить краще за класичний',18,4,1),(60,'Ні, це рідкість',18,1,1),(61,'Буде оточувати, коли любовний інтерес потурбується про це',18,3,1),(62,'Ні, всередині його халупи і так досить весело',19,4,1),(63,'Так, йому треба топати на край світу, ну бо так',19,1,1),(64,'Так, бо в його хату чомусь не телепортуються любовні інтереси',19,3,1),(65,'А що... в нього було помешкання? Його не спалили?',19,2,1),(66,'Так, це така гостросюжетка',20,4,1),(67,'Так, якщо це магічний артефакт, від якого залежить доля світу',20,1,1),(68,'Якби ж то. Якби ж то в нього була чашка і комод...',20,2,1),(69,'Так, неодмінно',21,1,1),(70,'Та ні, необов\'язково',21,4,1),(71,'Немає нічого гострішого за любовні перипетії',21,3,1);
