INSERT INTO `_questions` VALUES (1,'Хто Ваш герой?',NULL),(2,'Чи має герой магію?',NULL),(3,'Що зробив би герой, якби лиходій запросив його на свій бік?',NULL),(4,'Чи бере участь герой у епічних битвах?',NULL),(5,'Що зробить герой, якщо взнає, що скоро може бути кінець світу?',NULL),(6,'Чи відправляється герой у подорож?',1),(7,'Чи має герой рятувати світ?',1),(8,'Чи має герой подолати темного володаря?',1),(9,'Чи завжди герой вчиняє так, як правильно?',2),(10,'Чи оточує героя безнадійність, мряка, зло?',2),(11,'Чи може герой бути антагоністом?',2),(12,'Чи має герой психологічну травму?',2),(13,'Чи є герой обранцем пророцтва?',1),(14,'Чи закохується героїня у неймовірно привабливого, на кількасот років старшого чоловіка?',3),(15,'Чи стане героїня безсмертною, якщо її покохає безсмертний герой?',3),(16,'Чи може чиста, невинна героїня змінити темного, порочного героя?',3),(17,'Чи є шанс, що якщо на початку героїня ненавидить героя, то в кінці вони будуть палко закохані?',3),(18,'Чи оточує героя магічний побут?',4),(19,'Чи обов\'язково для сюжету герою покидати своє помешкання?',4),(20,'Чи може бути рушієм сюжету намагання дістати чашку, яка впала за комод?',4),(21,'Чи має сюжет бути гострим та динамічним?',4);
